package com.optifoundry.bridge.impl; import com.optifoundry.bridge.VersionBridge; import org.bukkit.Chunk; import java.util.*; public final class BridgeV112 implements VersionBridge{ private final Set<String> forced=new HashSet<>(); private String key(Chunk ch){return ch.getWorld().getName()+":"+ch.getX()+","+ch.getZ();} public boolean isForceloadSupported(){return false;} public boolean isChunkForceLoaded(Chunk ch){return forced.contains(key(ch));} public void setChunkForceLoaded(Chunk ch, boolean v){ if(v) forced.add(key(ch)); else forced.remove(key(ch)); } }